import React from 'react';

const styles = {
  container: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
    textAlign: 'center',
    border: '1px solid #ccc',
    borderRadius: '5px',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
  },
  header: {
    marginBottom: '20px',
  },
  section: {
    marginBottom: '20px',
    textAlign: 'left',
  },
  footer: {
    marginTop: '20px',
    color: '#888',
    fontSize: '14px',
  },
};

function DirectIPNotAllowed() {
  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={{ fontSize: '24px', color: '#333' }}>Error 1003</h1>
        <h2 style={{ fontSize: '20px', color: '#555' }}>Direct IP access not allowed</h2>
      </header>
      <section style={styles.section}>
        <div>
          <h3 style={{ fontSize: '18px', color: '#777' }}>What happened?</h3>
          <p style={{ fontSize: '16px', lineHeight: '1.6' }}>
            You've requested an IP address that is part of the rachakondadharmendra.info network. A valid Host header must be supplied to reach the desired website.
          </p>
        </div>
        <div>
          <h3 style={{ fontSize: '18px', color: '#777' }}>What can I do?</h3>
          <p style={{ fontSize: '16px', lineHeight: '1.6' }}>
            If you are interested in learning more, please visit the official website <a href="https://rachakondadharmendra.info" style={{ color: '#007bff', textDecoration: 'none' }}>rachakondadharmendra.info</a>.
          </p>
        </div>
      </section>
      <footer style={styles.footer}>
        <p>Performance & security by rachakondadharmendra.info</p>
      </footer>
    </div>
  );
}

export default DirectIPNotAllowed;
