import React, { useState, useEffect } from 'react';
import './DirectIPNotAllowed.css'; // Import custom CSS file for styling

function DirectIPNotAllowed() {
  const [clientIP, setClientIP] = useState(null);

  useEffect(() => {
    fetch('https://api.ipify.org?format=json')
      .then(response => response.json())
      .then(data => {
        setClientIP(data.ip);
      })
      .catch(error => {
        console.error('Error fetching IP address:', error);
      });
  }, []);

  const currentTime = new Date().toUTCString();

  return (
    <div className="full-screen-container">
      <div className="error-container">
        <header>
          <h1>Error 1003</h1>
          <h2>Direct IP access not allowed</h2>
        </header>
        <section>
          <div className="section-content">
            <h3>What happened?</h3>
            <p>You've requested an IP address directly. Direct IP access is not allowed.</p>
          </div>
          <div className="section-content">
            <h3>What can I do?</h3>
            <p>If you are interested in accessing this website, please use the correct domain name.</p>
          </div>
          {clientIP && (
            <div className="section-content">
              <p>Client IP: {clientIP}</p>
              <p>Current Time (UTC): {currentTime}</p>
            </div>
          )}
        </section>
        <footer>
          <p>Performance & security by rachakondadharmendra.info</p>
        </footer>
      </div>
    </div>
  );
}

export default DirectIPNotAllowed;
